mapper.update:
update tb_award
award_name = #{awardName}, award_desc = #{awardDesc}, award_img = #{awardImg}, point = #{point}, last_edit_time = #{lastEditTime}, enable_status = ${enableStatus}
where award_id = #{awardId};

mapper.select:
select award_id,award_name, award_desc, award_img, point,
       shop_id, priority, create_time, last_edit_time, enable_status
from tb_award where award_id = #{awardId}

select award_id,award_name, award_desc, award_img, point,
       shop_id, priority, create_time, last_edit_time, enable_status
from tb_award

    and award_name like '%${awardCondition.awardName}%'
    and award_name like '%${awardCondition.awardDesc}%'
    and shop_id = #{awardCondition.shopId}
    and enable_status = ${awardCondition.enableStatus}

order by point desc limit #{rowIndex},#{pageSize};
