mapper.select:
select psd.total,psd.days,psd.shop_id,s.shop_name,psd.product_id,p.product_name
from tb_product_sell_daily psd
left join tb_shop s on s.shop_id = psd.shop_id
left join tb_product p on p.product_id = psd.product_id

    and psd.shop_id = #{productSellDaily.shop.shopId}
    and s.shop_name like '%${productSellDaily.shop.shopName}%'
    and psd.product_id = #{productSellDaily.product.productId}
    and p.product_name = #{productSellDaily.product.productName}
    and psd.days between #{beginTime} and #{endTime}
    and psd.days >= #{beginTime}
    and psd.days <= #{endTime}

mapper.insert:
insert into tb_product_sell_daily(
    select product_id,shop_id,count(product_id) as total,days from(select shop_id,product_id,DATE_FORMAT(create_time,'%Y-%m-%d') as days from tb_user_product_map) a
    where a.days = DATE_SUB(CURDATE(),interval 1 day)
    group by a.shop_id,a.product_id,a.days
);
